import React from "react";
import Navbar from "../../components/Navbar";
import SubHeading from "../../components/SubHeading";


import { useParams } from "react-router-dom";

const data = require("../../dummy-data.json").blogPosts;



export default function BlogPage() {
    const { blogId } = useParams();
     // filter blogs by id instead of index, index heavily unreliable
    
    const blog = data.find((x) => {
        if (x.id == blogId) {
            return x
          }
        });
    
      
    

    
    function printWank() {
        console.log("wank");
    }

    function printHI() {
        console.log("HI");
        printWank();
    }

    function printThere() {
        setTimeout(() => {
            console.log("There");
        }, 0);
    }

    function printIX() {
        console.log("IX");
    }

    printHI();
    printThere();
    printIX();

   


    function BlogAuthor() {
        if (blog.author != undefined) {
            return <h2>Written by: { blog.author.firstName+" "+blog.author.lastName }</h2>;
        } else {
            return false;
        }
    }
    
        
    

    return (
        <div> 
            <Navbar/>
            <SubHeading subHeading={blog.title} style="font-size: 144px;"/>
            <h1>This is blog { blogId }</h1>
            <BlogAuthor/>
            
            
        </div>
    );
}
