import React from "react";
import Navbar from "../../components/Navbar";
import SubHeading from "../../components/SubHeading";


import { useParams } from "react-router-dom";

const data = require("../../dummy-data.json");



export default function BlogPage() {
    const { blogId } = useParams();
    const blog = data.blogPosts[blogId-1];


    console.log(blogId);

    function BlogAuthor() {
        if (blog.author.toString() != undefined) {
            return <h2>Written by: { blog.author.firstName+" "+blog.author.lastName }</h2>;
        } else {
            return false;
        }
    }
    
        
    

    return (
        <div> 
            <Navbar/>
            <SubHeading subHeading={blog.title} style="font-size: 144px;"/>
            <h1>This is blog { blogId }</h1>
            <BlogAuthor/>
            
            
        </div>
    );
}
