import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";


import "bootstrap/dist/css/bootstrap.min.css";

import Navbar from "../../components/Navbar";
import Heading from "../../components/Heading";
import BlogList from "../../components/BlogList";
import Footer from "../../components/Footer";

import "../../App.css";
import "./index.css";


// Week 1: Import the blogPosts and categories from the dummy-data.json file
const data = require("../../dummy-data.json");
let blogPostsData = data.blogPosts;
const categoriesData = data.categories;

export default function BlogsPage() {

  const [categoryId, setCategoryId] = useState(undefined);
  const [blogs, setBlogs] = useState(blogPostsData);
  const [displayBlogs, setDisplayBlogs] = useState(blogPostsData);
  
  const { categoryIdPassed } = useParams();

  blogPostsData = blogPostsData.filter((x) =>
    categoryIdPassed !== undefined
      ? x.categories.find((y) => y.id.toString() === categoryIdPassed)
    : true
  );



  useEffect(() => {
    if (categoryId) {
      const filteredBlogs = blogPostsData.filter((blog) => {
        return blog.categories.some((category) => category.id === categoryId);
      });
      setBlogs(filteredBlogs);
    }
  }, [categoryId])

  const CategoriesList = ({categoryId}) => {
    return categoriesData.map((category) => {
      return categoryId === category.id.toString() ? (
        <button
          key={category.id}
          onClick={() => setCategoryId(category.id)}
          style={{ color: "blue" }}
        >
          <p key={category.id}>{category.title}</p>
        </button>
      ) : (
        <button
          key={category.id}
          onClick={() => setCategoryId(category.id)}
          style={{ color: "black" }}
        >
          <p key={category.id}>{category.title}</p>
        </button>
      );
    });
  }



  return (
    <>
      <Navbar />
      <div className="container">
        <Heading />
        <div className="scroll-menu">
          <CategoriesList 
              categoriesData={categoriesData} 
              categoryId={categoryId}
              setCategoryId={setCategoryId}>
          </CategoriesList>
        </div>
        <div style={{ display: "flex", justifyContent: "space-between" }}>
          <p className="page-subtitle">Blog Posts</p>
        </div>
        <BlogList blogPosts={blogs} />
      </div>
      <Footer />
    </>
  );
}
